function test_common_functions(fn, param) {

    if(typeof fn === "function"){
        //console.log("its a function")
    }
    //console.log("test")

    switch(fn){
        case "test":
            test(param);
            break;
        case "sum":
            return sum(param[0], param[1]);
		case "validateEmailAddress":
            return validateEmailAddress(param[0])
        default:
            break;
    }
}


function validateEmailAddress(emailStr){
    // Use JavaScript coercion to guarantee emailStr is a string
    emailStr = emailStr + '';
    // Compare emailStr against the allowed syntax as specified in the regular expression
    // If emailStr has allowed syntax, return true, else return false
    if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
      return true;
    }
    else {
     return false;
    }
}

function sum(a,b){
    return a+b;
}

